import './App.css'

import React, {useState, useEffect} from 'react'

/* React code below based on https://reactjs.org/tutorial/tutorial.html */

declare global {

  interface Pyodide {
    runPythonAsync: any
  }

  interface Window {
    languagePluginLoader: any
    pyodide: Pyodide
  }
}

function Square(i: number) {
  return (
    <button className="square">
      {/* TODO */}
    </button>
  );
}

function Board() {
  const status = 'Next player: X';

  return (
    <div>
      <div className="status">{status}</div>
      <div className="board-row">
        {Square(0)}
        {Square(1)}
        {Square(2)}
      </div>
      <div className="board-row">
        {Square(3)}
        {Square(4)}
        {Square(5)}
      </div>
      <div className="board-row">
        {Square(6)}
        {Square(7)}
        {Square(8)}
      </div>
    </div>
  );
}

function Game(){
  return (
    <div className="game">
      <div className="game-board">
        <Board />
      </div>
      <div className="game-info">
        <div>{/* status */}</div>
        <ol>{/* TODO */}</ol>
      </div>
    </div>
  );
}

async function loadNeuralNetScripts()  {
  let response = await fetch('/NeuralNet.py')
  let mlcode = await response.text()
  window.pyodide.runPythonAsync(mlcode)
}


function App() {
  const [console, setConsole] = useState('Initializing Python 3.8\n')
  const [pythonLoaded, setPythonLoaded] = useState(false)
  //const [NNScripts, setNNScripts] = useState(false)

  //Initialize Python
  useEffect(() => {
    window.languagePluginLoader.then(() => {
      setConsole(console + 'Python Loaded!\n')
      setPythonLoaded(true)
    })
  }, [pythonLoaded])

  //load NeuralNet.py Scripts
  useEffect(() => {
    if(false){
      setConsole(console + 'Loading Neural Net Scripts!\n')
      loadNeuralNetScripts()
      .then(() => {
        setConsole(console + 'Neural Net Scripts Loaded!\n')
      })
      .catch((err: Error) => setConsole(console + err + '\n'))
    }
  }, [pythonLoaded])


  return (
    <div className="App">        
        <div className='python-console'>{console}</div>
        <Game />
    </div>

  )
}



// ========================================

export default App;
