import React from 'react';
import './App.css';


function App() {
  return (
    <div className="App">
      <header className="App-header">
        <p>Welcome to the Tic Tac Toe Neural Net! This project is currently under development.</p>
        <p>This is a Neural Net consisting of a single fully connected layer (no hidden layers) with a sigmoid activation function and cross-entropy loss</p>
        <textarea id='python-console'>{output}</textarea>
      </header>
    </div>
  );
}


export default App;

let output : string = "";
const code = document.getElementById("code");
const weights = document.getElementById("weights");

function addToOutput(s : string) {
    output.value += '>>>' + code.value + '\n' + s + '\n';
}

output.value = 'Initializing...\n';
// init pyodide and load Relevant Python Scripts
languagePluginLoader.then(() => {
    output.value += 'Python Loaded!\n';
    loadNeuralNetScripts();
});

async function evaluatePython() {
    pyodide.runPythonAsync(code.value)
        .then(output => {
            addToOutput(output);
            updateTable();
        })
        .catch((err) => {
            addToOutput(err)
        });
}

async function loadNeuralNetScripts() {
    response = await fetch('/NeuralNet.py');
    mlcode = await response.text();
    pyodide.runPythonAsync(mlcode)
        .then(() => {
            output.value += 'Neural Net Scripts Loaded!\n';
            updateTable();
        })
        .catch((err) => output.value += err);
}

async function updateTable() {
    var table = document.getElementById("weights");
    var rows = table.rows;
    var weights = await pyodide.globals.WEIGHTS;
    for (i = 0; i < weights.length; i++) {
        var row = rows.item(i + 1);
        var vals = weights[i];
        for (j = 0; j < vals.length; j++) {
            try {
                row.deleteCell(j + 1);
            } catch (err) {
                // console.log(err)
            }
            var cell = row.insertCell(j + 1);
            cell.innerText = parseFloat(vals[j]).toFixed(4);
        }
    }
}
