import React, {useState, useEffect} from 'react'
import './App.css'

/* React code below based on https://reactjs.org/tutorial/tutorial.html */

interface IAppProps {
}
interface IAppState {
  console: string
}

declare global {
  interface Window {
    languagePluginLoader: any
  }
}

function Square(i: number) {
  return (
    <button className="square">
      {/* TODO */}
    </button>
  );
}

function Board() {
  const status = 'Next player: X';

  return (
    <div>
      <div className="status">{status}</div>
      <div className="board-row">
        {Square(0)}
        {Square(1)}
        {Square(2)}
      </div>
      <div className="board-row">
        {Square(3)}
        {Square(4)}
        {Square(5)}
      </div>
      <div className="board-row">
        {Square(6)}
        {Square(7)}
        {Square(8)}
      </div>
    </div>
  );
}

function Game(){
  return (
    <div className="game">
      <div className="game-board">
        <Board />
      </div>
      <div className="game-info">
        <div>{/* status */}</div>
        <ol>{/* TODO */}</ol>
      </div>
    </div>
  );
}

function App(){
  const [console, setConsole] = useState("Initializing Python 3.8\n");

  useEffect(() => {
    window.languagePluginLoader.then(() => {
      //setConsole(console + 'Python Loaded!\n')
    })
  })

  return (
    <div className="App">        
        <textarea id='python-console' value={console} readOnly/>
        <Game />
    </div>

  )
}



// ========================================

export default App;
