import './App.css'

import React, {useState, useEffect} from 'react'

/* React code below based on https://reactjs.org/tutorial/tutorial.html */

const dec = 4
let train_data = require('./train_data.json');
let test_data = require('./test_data.json');


declare global {

  interface pythonVars {
    WEIGHTS: number[][]
    BIASES: string[]
  }

  interface Pyodide {
    runPythonAsync: any
    globals: pythonVars
  }

  interface Window {
    languagePluginLoader: any
    pyodide: Pyodide
  }
}

type boardVal = number | null
type boardArray = [boardVal, boardVal, boardVal, boardVal, boardVal, boardVal, boardVal, boardVal, boardVal]
type classLabel = 0 | 1 | 2  // Class X Wins, Y wins, and Draw

function Intro() {
  return (
    <div>
      <h2>Welcome to the Tic Tac Toe Neural Net!</h2>
        <p>The networks consists of a single fully connected layer with a sigmoid activation function and cross-entropy loss function</p>
      <p>The goal of this project is to train a simple neural net to classify complete games of Tic Tac Toe as "X-Wins", "O-Wins", or "Draw".</p>
      <p>To expand the problem space beyond the finite number of possible 3x3 Tic Tac Toe boards, Xs and Os are represented by random values in the range (0, 0.5) and (0.5, 1) respectively.</p>
  </div>
  )
}

function calculateWinner(squares: boardArray) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];

    if(!squares[a] || !squares[b] || !squares[c]){
      continue  // skip loop if any of the cells in the line are empty
    }

    if (squares[a] < 0.5 && squares[b] < 0.5 && squares[c] < 0.5){
      return 0 // Class X wins
    } else if (squares[a] >= 0.5 && squares[b] >= 0.5 && squares[c] >= 0.5) {
      return 1; // Class Y wins
    }
  }
  if(squares.includes(null)){
    return null // game incomplete, no class applicable
  } else {
    return 2 // Class Draw
  }
}

function Square(props: any) {

  return (
    <button className="square" 
      disabled={props.i == null? false: true} 
      onClick={props.onClick}
      style={props.i == null ? {background: "#fff"} : props.i > 0.5 ? {background: "#e4b5b5"} : {background: "#99b1f4"}}
      >
      {props.i ? props.i.toFixed(dec): props.i}
    </button>
  )
}

function Board() {
  const [xNext, setXNext] = useState(true)
  const [squares, setSquares] = useState(Array(9).fill(null) as boardArray)
  const [status, setStatus] = useState("Next player: X")

  function handleClick(i: number){
    let sq: boardArray = squares.slice() as boardArray
    sq[i] = xNext ? Math.random()/2 : 0.5 + Math.random()/2
    let st = calculateWinner(squares)
    switch(st){
      case 0:
        setStatus("X Wins!")
        break;
      case 1:
        setStatus("O Wins!")
        break;
      case 2:
        setStatus("Draw")
        break;
      case null:
        setStatus(!xNext ? "Next player: X": "Next player: O")
        break;
    }
    // update state, but won't take effect until after render
    setSquares(sq)
    setXNext(!xNext)
  }

  return (
    <div className="game-board">
      <div className="status">{status}</div>
      <div className="board-row">
        <Square i={squares[0]} onClick={() => handleClick(0)}/>
        <Square i={squares[1]} onClick={() => handleClick(1)}/>
        <Square i={squares[2]} onClick={() => handleClick(2)}/>
      </div>
      <div className="board-row">
        <Square i={squares[3]} onClick={() => handleClick(3)}/>
        <Square i={squares[4]} onClick={() => handleClick(4)}/>
        <Square i={squares[5]} onClick={() => handleClick(5)}/>
      </div>
      <div className="board-row">
        <Square i={squares[6]} onClick={() => handleClick(6)}/>
        <Square i={squares[7]} onClick={() => handleClick(7)}/>
        <Square i={squares[8]} onClick={() => handleClick(8)}/>
      </div>
    </div>
  );
}

